import React, { useState } from "react";
import { Card, Grid, Image, Button, Form } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";

function BookSearchContainer(props) {
  //   return this.props.user ? (

  const [input, setInput] = useState({
    searchText: "",
  });

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: [e.target.value],
    });
  };

  const handleSearchSubmit = (e) => {
    return "hi";
  };

  return (
    <Form onChange={handleChange}>
      <Form.Field>
        <label>Search Books</label>
        <input placeholder="Search" name="search" value={input.searchText} />
      </Form.Field>
      <Form.Field>
        <label>First Name</label>
        <input placeholder="First Name" />
      </Form.Field>
      <Button onClick={handleSearchSubmit}>Search</Button>
    </Form>
  );
}

const mapStateToProps = (state) => {
  return {
    user: state.user.user,
  };
};

export default withRouter(connect(mapStateToProps)(BookSearchContainer));
