import { connect } from "react-redux";
import Moment from "react-moment";

function Post(props) {
  if (props.user.id === props.post.user.id) {
    return (
      <Comment>
        <Comment.Content>
          <Comment.Author as="a">{props.post.user.username}</Comment.Author>
          <Comment.Metadata>
            <div>
              <Moment fromNow>{props.post.created_at}</Moment>
            </div>
          </Comment.Metadata>
          <Comment.Text>{props.post.content}</Comment.Text>
          <Comment.Actions>
            <Comment.Action onClick={PostForm}>Edit Post</Comment.Action>
