export function userReducer(state = { user: null, loggedIn: false }, action) {
  switch (action.type) {
    case "SET_CURRENT_USER":
        let loggedIn = false
        if(!state.user === null){
            loggedIn = true
        }
      return {
        ...state,
        user: action.payload,
        loggedIn: true,
      };
    case "AUTHENTICATING_USER":
      return {
        ...state,
        message: "Authenticating user",
      };
    case "FAILED_LOGIN":
      return {
        ...state,
        message: "Login failed",
      };
    default:
      return state;
  }
}
