export function userReducer(state = { loggedIn: false }, action) {
  switch (action.type) {
    case "SET_CURRENT_USER":
      return {
        ...state,
        user: action.payload,
        loggedIn: true,
      };
    case "AUTHENTICATING_USER":
      return {
        ...state,
        message: "Authenticating user",
      };
    case "FAILED_LOGIN":
      return {
        ...state,
        message: "Login failed",
      };
    default:
      return state;
  }
}
