import React from "react";
import { Button, Item, Grid, Card, ItemContent } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";
import { setGroupBook } from "../actions/group";

function ProfilePage(props) {
  const handleGroupBookClick = (groupBook) => {
    props.setGroupBook(groupBook);
  };

  return (
    <div>
      <h1>{props.user.username}'s Profile</h1>
      <h3>Member of:</h3>
      <Item.Group divided>
        {props.userGroups.map((userGroup) => {
          return (
            <Item>
                <Link to={`/groups/`}>
                <Item.Header>{userGroup.name}</Item.Header>
                </Link>
              <ItemContent>{userGroup.description}</ItemContent>
              <ItemContent>
                {userGroup.books.map((book) => {
                  return <h2>{book.title}</h2>;
                })}
              </ItemContent>
            </Item>
          );
        })}
      </Item.Group>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    userGroups: state.user.userGroups,
    user: state.user.user,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setGroupBook: (group) => dispatch(setGroupBook(group)),
  };
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(ProfilePage)
);
