import React, { useState, useEffect } from "react";
import { Card, Grid, Image, Button, Form } from "semantic-ui-react";
import { connect, useSelector } from "react-redux";
import { withRouter } from "react-router-dom";
import { setMostPopular, setSearchedBooks } from "../actions/book";
import BookSearchItem from "./BookSearchItem";

function BookSearchContainer(props) {
  //   return this.props.user ? (

  const [input, setInput] = useState({
    search: "",
  });

  const [mostPopular, setMostPopular] = useState({
    mostPopular: [],
  });

  const [searchedBooks, setSearchedBooks] = useState({
    searchedBooks: [],
  });

  useEffect(() => {
    let slicedBooks = null;
    fetch("http://localhost:3000/books")
      .then((resp) => resp.json())
      .then((books) => {
        function compare(a, b) {
          const groupBooksA = a.group_books.length;
          const groupBooksB = b.group_books.length;

          let comparison = 0;
          if (groupBooksA > groupBooksB) {
            comparison = -1;
          } else if (groupBooksA < groupBooksB) {
            comparison = 1;
          }
          return comparison;
        }
        books.sort(compare).slice(0, 12);
        slicedBooks = books.slice(0, 12);
      })
      .then(() => {
        props.setMostPopular(slicedBooks);
        // setMostPopular({
        //   ...mostPopular,
        //   mostPopular: slicedBooks,
        // });
        // console.log(mostPopular);
      });
  }, []);

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: [e.target.value],
    });
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    console.log(props.mostPopular);
    return "hi";
  };

  return (
    <div>
      <h1>Find a Book!</h1>
      <Form>
        <Form.Field>
          <label>Search Books</label>
          <input
            onChange={handleChange}
            placeholder="Search"
            name="search"
            value={input.search}
          />
        </Form.Field>
        <Button onClick={handleSearchSubmit}>Search</Button>
      </Form>
      if (props.sea)
      {props.searchedBooks === null && props.mostPopular !== null ? (
        props.mostPopular.map((book) => {
          return <BookSearchItem key={book.id} book={book} />;
        })
      ) : (
        <h2>Loading</h2>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    mostPopular: state.books.mostPopular,
    searchedBooks: state.books.searchedBooks,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    setMostPopular: (mostPopularBooks) =>
      dispatch(setMostPopular(mostPopularBooks)),
    setSearchedBooks: (searchedBooks) =>
      dispatch(setSearchedBooks(searchedBooks)),
  };
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(BookSearchContainer)
);
