import React, { useState, useEffect } from "react";
import { Card, Grid, Image, Button, Form } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { setMostPopular, setSearchedBooks } from "../actions/book";

function BookSearchContainer(props) {
  //   return this.props.user ? (

  const [input, setInput] = useState({
    search: "",
  });

  useEffect = () => {
    if (props.searchedBooks === null) {
      fetch("http://localhost:3000/books")
        .then((resp) => resp.json())
        .then((books) => {
          debugger;
        });
    }
  };

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: [e.target.value],
    });
  };

  const handleSearchSubmit = (e) => {
    return "hi";
  };

  return (
    <div>
      <h1>Find a Book!</h1>
      <Form>
        <Form.Field>
          <label>Search Books</label>
          <input
            onChange={handleChange}
            placeholder="Search"
            name="search"
            value={input.search}
          />
        </Form.Field>
        <Button onClick={handleSearchSubmit}>Search</Button>
      </Form>
      <h2>Most Popular Books</h2>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    mostPopular: state.books.mostPopular,
    searchedBooks: state.books.searchedBooks,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    set
  };
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(BookSearchContainer)
);
