  //   }
  // }

  useEffect(() => {
    if (localStorage.getItem("token")) {
      fetch("http://localhost:3000/decode_token", {
        headers: {
          Authenticate: localStorage.token,
        },
      })
        .then((res) => res.json())
        .then((userData) => {
          props.setCurrentUser(userData);
          props.setUserGroups(userData.groups);
          console.log("No token found, user is not authenticated");

          //if error, don't update the state
        });
    } else {
      console.log("No token found, user is not authenticated");
    }
  }, []);
  return (
    <div>
      <Nav
      // user={this.state.currentUser}
      // updateCurrentUser={this.updateCurrentUser}
      />
      <Switch>
        <Route exact path="/" render={() => <LandingPage />} />
        <Route
          exact
          path="/profile"
          render={() => {
            return props.user !== null ? <Profile /> : <Redirect to="/login" />;
          }}
        />
        <Route
          exact
          path="/browse-books"
          render={() => <BookSearchContainer />}
        />
        <Route
          exact
          path="/browse-groups"
          render={() => <GroupSearchContainer />}
        />
        <Route exact path="/groups/:id" render={() => <GroupPage />} />
        <Route
          exact
          path="/login"
          render={() =>
            props.user === null ? (
              // <LoginForm updateCurrentUser={this.updateCurrentUser} />
              <LoginForm />
            ) : (
              <Redirect to="/" />
            )
          }
        />
        <Route component={NotFound} />
      </Switch>
    </div>
  );
}

const mapStateToProps = (state) => ({
  user: state.user.user,
  loggedIn: state.user.loggedIn,
});

const mapDispatchToProps = (dispatch) => {
  return {
    setCurrentUser: (user) => dispatch(setCurrentUser(user)),
    setUserGroups: (userGroups) => dispatch(setCurrentUser(userGroups)),
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App));
