import React, { useState } from "react";
import { Card, Grid, Image, Button, Form } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";

function CreateGroupForm(props) {

  const [input, setInput] = useState({
    search: "",
  });

  fetch("http://localhost:3000/users", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      user: {
        name: input.username,
        description: input.password,
      },
    }),
  })
    .then((res) => res.json())
    .then((userData) => {
      console.log("response from the server", userData);
      if (userData.error_message) {
        alert("There are errors in the form, fix them");
      } else {
        // debugger
        localStorage.setItem("token", userData.token);
        // localStorage.setItem("userId", userData.user_data.id)   //added this to store current user

        this.props.setCurrentUser(userData.user_data.user);
      }
    });
}
