import React from "react";
import { Button, Item, Grid, Card } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter, Link } from "react-router-dom";
import { setGroupBook } from "../actions/group";

function ProfilePage(props) {
  const handleGroupBookClick = (groupBook) => {
    props.setGroupBook(groupBook);
  };

  return (
    <div>
      <h1>{props.user.user.username}'s Profile</h1>
      <h3>Member of:</h3>
      <Item.Group divided>
        {props.userGroups.map((userGroup) => {
          return (
            <Item>
              <Link
                to=""
                onClick={() => {
                  handleGroupBookClick(userGroup);
                }}
              >
                <Item.Image size="small" src={userGroup.book.thumbnail} />
              </Link>
              <Item.Content style={{ marginLeft: "20px" }}>
                <Link
                  to=""
                  onClick={() => {
                    handleGroupBookClick(userGroup);
                  }}
                >
                  <Item.Header>{userGroup.book.title}</Item.Header>
                </Link>
                <Item.Meta>
                  <span>{userGroup.book.author}</span>
                </Item.Meta>
                <Item.Description>
                  <p>{userGroup.status}</p>
                </Item.Description>
              </Item.Content>
            </Item>
          );
        })}
      </Item.Group>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    userGroups: state.user.userGroups,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setGroupBook: (group) => dispatch(setGroupBook(group)),
  };
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(ProfilePage)
);
