import React, { useState, useEffect } from "react";
import { NavLink, withRouter, Link } from "react-router-dom";
import { Menu, Button, Image, Item } from "semantic-ui-react";
import { setCurrentUser } from "../actions/user";
import { connect } from "react-redux";

function AdminPanel(props) {
  const [editButton, setEditButton] = useState(true);
  const [manageBooksButton, setManageBooksButton] = useState(false);
  const [manageUsersButton, setManageUsersButton] = useState(false);

  const [groupData, setGroupData] = useState([]);

  const handleBooksClick = () => {
    setMyBooksShow(true);
    setMyClubsShow(false);
    setAdminShow(false);
  };

  const handleClubsClick = () => {
    setMyBooksShow(false);
    setMyClubsShow(true);
    setAdminShow(false);
  };

  const handleAdminClick = (group) => {
    setMyBooksShow(false);
    setMyClubsShow(false);
    setAdminShow(true);
    setSelectedGroup(group);
  };
  useEffect(() => {
    fetch(`http://localhost:3000/groups/${props.selectedGroup.id}`)
      .then((resp) => resp.json())
      .then((groupData) => {
        setGroupData(groupData);
      });
  }, []);
  return (
    <div>
      <h1>{props.selectedGroup.name} Admin Panel</h1>
      <Menu compact={true} style={{ marginLeft: "auto", marginRight: "auto" }}>
        <Menu.Item name="edit club info" onClick={setEditButton}>Edit Club Info</Menu.Item>
        <Menu.Item name="manage club books" onClick={setManageBooksButton}>Manage Club Books</Menu.Item>
        <Menu.Item name="manage club users" onClick={setManageUsersButton}>Manage Club Users </Menu.Item>
      </Menu>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    // user: state.user.user,
    user: state.user,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setCurrentUser: (user) => dispatch(setCurrentUser(user)),
  };
};
export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(AdminPanel)
);
