import React, { useState, useEffect } from "react";
import { Card, Button, Image, Header, Modal } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { setGroupBooks, setGroupUsers } from "../actions/group";

function GroupPage(props) {
  useEffect(() => {
    let slicedBooks = null;
    fetch("http://localhost:3000/books")
      .then((resp) => resp.json())
      .then((books) => {
        function compare(a, b) {
          const groupBooksA = a.group_books.length;
          const groupBooksB = b.group_books.length;

          let comparison = 0;
          if (groupBooksA > groupBooksB) {
            comparison = -1;
          } else if (groupBooksA < groupBooksB) {
            comparison = 1;
          }
          return comparison;
        }
        books.sort(compare).slice(0, 12);
        slicedBooks = books.slice(0, 12);
      })
      .then(() => {
        props.setMostPopularBooks(slicedBooks);
        // setMostPopular({
        //   ...mostPopular,
        //   mostPopular: slicedBooks,
        // });
        // console.log(mostPopular);
      });
  }, []);
  return (
    <div>
      <h1>hi</h1>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    goupBooks: state.groups.groupBooks,
    groupUsers: state.groups.groupUsers,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setGroupBooks: (group) => dispatch(setGroupBooks(group)),
    setGroupUsers: (group) => dispatch(setGroupUsers(group)),
  };
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(GroupPage)
);
