export function bookReducer(
  state = { mostPopular: null, searchedBooks: null },
  action
) {
  switch (action.type) {
    case "SET_MOST_POPULAR":
      let newState = { ...state, mostPopular: action.payload };
      return {
        newState,
      };
    case "SET_SEARCHED_BOOKS":
      return {
        ...state,
        searchedBooks: action.payload,
      };
    default:
      return state;
  }
}
