import React from "react";
import { NavLink, withRouter, Link } from "react-router-dom";
import { Menu, Button, Modal } from "semantic-ui-react";
import { setCurrentUser, setSelectedUser } from "../actions/user";
import { connect } from "react-redux";

function Nav(props) {
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    props.setCurrentUser(null);
  };

  return (
    <div>
      {props.user ? (
        <Menu>
          <Menu.Item header>Flatiron Book Club</Menu.Item>

          <Menu.Item as={NavLink} to="/browse-books" name="Browse Books" />
          <Menu.Item as={NavLink} to="/browse-groups" name="Browse Groups" />

          <Menu.Menu position="right">
            <Menu.Item
              as={NavLink}
              to={`/users/${props.user.id}`}
              name={props.user.username}
              onClick={setSelectedUser()}
            />
            <Menu.Item>
              <Link to="/" onClick={logout}>
                <Button>Log Out</Button>
              </Link>
            </Menu.Item>
          </Menu.Menu>
        </Menu>
      ) : (
        <div>
          <Menu>
            <Menu.Item header>Flatiron Book Club</Menu.Item>
            <Menu.Item as={NavLink} to="/browse-books" name="Browse Books" />
            <Menu.Item
              as={NavLink}
              to="/browse-groups"
              name="Browse Groups"
              // active={pathname === "/browse-groups"}
            />
            <Menu.Menu position="right">
              <Menu.Item>
                <Link to="/login">
                  <Button>Sign In</Button>
                </Link>
              </Menu.Item>
              <Menu.Item>
                <Link to="/create-account">
                  <Button primary>Sign Up</Button>
                </Link>
              </Menu.Item>
            </Menu.Menu>
          </Menu>
        </div>
      )}
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    user: state.user.user,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setCurrentUser: (user) => dispatch(setCurrentUser(user)),
  };
};
export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Nav));
