export function user(state={}, action){
    switch (action.type) {
        case "SET_CURRENT_USER":
          return {
            ...state,
            user: action.payload
          };
        case "AUTHENTICATING_USER":
          return {
            ...state,
            message: "authenticating user"
          };
        case "FAILED_LOGIN":
          return {};
        default:
          return state
      }

}
