import React, { useState, useEffect } from "react";
import { Card, Button, Image, Header, Form, Modal } from "semantic-ui-react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";

function BookSearchItem(props) {
  return (
    <div>
      <Modal
        trigger={
          <Card style={{ display: "inline-block" }}>
            <Image src={props.book.thumbnail} wrapped ui={false} />
            <Card.Content>
              <Card.Header>{props.book.title}</Card.Header>
              <Card.Meta>
                <span>By: {props.book.author}</span>
              </Card.Meta>
              {/* <Card.Description>
          Matthew is a musician living in Nashville.
        </Card.Description> */}
            </Card.Content>
          </Card>
        }
      >
        <Modal.Header>Select a Photo</Modal.Header>
        <Modal.Content image>
          <Image src={props.book.thumbnail} wrapped size={medium} />{" "}
          <Modal.Description>
            <Header>{props.book.title}</Header>
            <p>{props.book.summary}</p>
            <p>{props.book.pages}</p>
            <p>{props.book.averageRating}</p>
          </Modal.Description>
        </Modal.Content>
      </Modal>
      ;
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    mostPopular: state.books.mostPopular,
    searchedBooks: state.books.searchedBooks,
  };
};

export default withRouter(connect(mapStateToProps)(BookSearchItem));
