import React, { useState, useEffect } from "react";
import { Form, Button, TextArea } from "semantic-ui-react";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";

function Post(props) {
  debugger;
  if (props.user.id === props.post.user.id) {
    return (
      <div >
        <h4>{props.post.user.username} says...</h4>
        <p style={{ display: "inline-block", paddingBottom: "10px" }}>{props.post.content}</p>
        <Button size="mini" style={{ display: "inline-block" }}>
          Edit Post
        </Button>
        <Button size="mini" style={{ display: "inline-block" }}>
          Delete Post
        </Button>
      </div>
    );
  } else {
    return (
      <div>
        <h4>{props.post.user.username} says...</h4>
        <p>{props.post.content}</p>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.user,
    group: state.groups.group,
    groupBook: state.groups.groupBook,
    groupUsers: state.groups.groupUsers,
  };
};

// const mapDispatchToProps = (dispatch) => {
//   return {
//     setBookPosts: (posts) => dispatch(setBookPosts(posts)),
//   };
// };

export default withRouter(connect(mapStateToProps, null)(Post));
