export function user(state={}, action){
    switch (action.type) {
        case "SET_CURRENT_USER":
          return {
            type: 'current',
            message: action.message
          };
        case "AUTHENTICATING_USER":
          return {
            type: 'alert-danger',
            message: action.message
          };
        case "FAILED_LOGIN":
          return {};
        default:
          return state
      }

}
